import { Component } from '@angular/core';
import { SharedService } from 'src/app/shared.service';
import { Route, Router, RouterModule } from '@angular/router';
import { catchError } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'angular';
  Username = '';
  Pass = '';

  isLogin = false;
  constructor(private sharedService: SharedService, private router: Router) {}
  async login(): Promise<void> {
    this.sharedService
      .login(this.Username, this.Pass)
      .pipe(
        catchError((error: any) => {
          console.log('Thất bại');
          throw error;
        })
      )
      .subscribe((response: any) => {
        console.log('Đăng nhập thành công');
        this.isLogin = true;
        this.router.navigate(['/department']);
        this.router.navigate(['/employee']);
      });
  }
}
<div class="container">
  <h3 class="d-flex justify-content-center">Angular, WebAPI, SQL Server</h3>
  <h5 class="d-flex justify-content-center">Employee Management Portal</h5>

  <nav class="navbar navbar-expand-sm bg-light navbar-dark">
    <ul class="navbar-nav">
      <li class="nav-item">
        <button
          routerLink="login"
          class="m-1 btn btn-light btn-outline-primary"
          Button
        >
          Login
        </button>
        <button
          routerLink="department"
          class="m-1 btn btn-light btn-outline-primary"
          Button
        >
          Department
        </button>
      </li>
      <li class="nav-item">
        <button
          routerLink="employee"
          class="m-1 btn btn-light btn-outline-primary"
          Button
        >
          Employee
        </button>
      </li>
    </ul>
  </nav>
  <router-outlet></router-outlet>
</div>
