import { Component, OnInit, Input } from '@angular/core';
import { SharedService } from 'src/app/shared.service';
import { Route, Router, RouterModule } from '@angular/router';
@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css'],
})
export class SignupComponent implements OnInit {
  constructor(private service: SharedService, private router: Router) {}

  Username: string = '';
  Pass: string = '';
  Rol: string = 'user'; // Mặc định là quyền "user"

  ngOnInit(): void {
    // Khởi tạo các giá trị mặc định (nếu cần)
  }

  addAccount(): void {
    // Kiểm tra các trường thông tin hợp lệ (nếu cần)

    const newUser = {
      Username: this.Username,
      Pass: this.Pass,
      Rol: this.Rol,
    };

    // Gọi phương thức đăng ký tài khoản trong service
    this.service.addAccount(newUser).subscribe(
      (response: any) => {
        console.log('Đăng ký thành công');
        alert('Đăng ký thành công');
        this.router.navigate(['/login']);
      },
      (error: any) => {
        console.log('Đăng ký thất bại');
        alert('Đăng ký thất bại');
      }
    );
  }
}
<div class="container">
  <form>
    <!-- Email input -->
    <div class="form-outline mb-4">
      <input
        type="email"
        class="form-control"
        placeholder="Username"
        id="username"
        [(ngModel)]="Username"
        name="Username"
        required
      />
    </div>
    <!-- Password input -->
    <div class="form-outline mb-4">
      <input
        type="password"
        class="form-control"
        placeholder="Password"
        id="password"
        [(ngModel)]="Pass"
        name="Pass"
        required
      />
    </div>
    <button
      style="padding: 10px 60px; border: none; border-radius: 4px"
      type="button"
      class="btn btn-primary btn-block mb-4"
      (click)="addAccount()"
    >
      Đăng Ký
    </button>
  </form>
</div>
