import { Component } from '@angular/core';
import { SharedService } from 'src/app/shared.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  username!: string;
  password!: string;

  constructor(private SharedService: SharedService) {}

  login(): void {
    const isLoggedIn = this.SharedService.login(this.username, this.password);
    if (isLoggedIn) {
      // Đăng nhập thành công, thực hiện các hành động sau khi đăng nhập
      console.log('Đăng nhập thành công');
      // Redirect hoặc thực hiện các tác vụ khác
    } else {
      // Đăng nhập không thành công, hiển thị thông báo lỗi hoặc xử lý tùy ý
      console.log('Đăng nhập không thành công');
    }
  }
}
<div class="container">
  <form>
    <!-- Email input -->
    <div class="form-outline mb-4">
      <input
        type="email"
        class="form-control"
        placeholder="Username"
        id="username"
        [(ngModel)]="username"
        required
      />
    </div>

    <!-- Password input -->
    <div class="form-outline mb-4">
      <input
        type="password"
        class="form-control"
        placeholder="Password"
        id="password"
        [(ngModel)]="password"
        required
      />
    </div>
    <button
      style="padding: 10px 60px; border: none; border-radius: 4px"
      type="button"
      class="btn btn-primary btn-block mb-4"
      (click)="login()"
    >
      Sign in
    </button>
  </form>
</div>
